mapper.update:
UPDATE users
SET password = #{password}
WHERE email = #{email}

UPDATE users
SET
nickname = #{nickname},
address = #{address},



password = #{password}
WHERE email = #{email}

mapper.insert:
INSERT INTO users_refresh_token (user_id, refresh_token)
    VALUES (#{userId}, #{refreshToken})
ON DUPLICATE KEY UPDATE refresh_token = #{refreshToken}

mapper.select:
SELECT refresh_token
FROM users_refresh_token
WHERE user_id = #{userId}
